{"arrows":".digital-slider.tparrows {
	cursor:pointer;
	background-color: transparent;
	position:absolute;
	display:block;
	z-index:100;
    height: 140px;
    line-height: 140px;
    width: 140px;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    transition: all 0.3s;
}
.digital-slider.tparrows:after {
	content: \'\';
    display: block;
    position: absolute;
    top: 50%;
    width: 1px;
    height: 80px;
    transform: translateY(-50%);
    -webkit-transform: translateY(-50%);
    -moz-transform: translateY(-50%);
    background-color: #e0e0e0;
}
.digital-slider.tp-leftarrow:after {
	right: 0;
}
.digital-slider.tp-rightarrow:after {
	left: 0;
}
.digital-slider.tparrows:before {
	font-family: \"Ionicons\";
	font-size: 34px;
	color: #fff;
	display:block;
	line-height: 140px;
	text-align: center;
    -webkit-transition: color 0.3s;
    -moz-transition: color 0.3s;
    transition: color 0.3s;
    z-index:2;
    position:relative;
}
.digital-slider.tparrows.tp-leftarrow:before {
    content: \"\\f124\";
}
.digital-slider.tparrows.tp-rightarrow:before {
    content: \"\\f125\";
}
.digital-slider.tparrows:hover:before {
 	color: #333;
}"}
