wp_revslider_navigations.css:
{"bullets":".slider .tp-bullet-bg {
	background-color: rgba(51, 51, 51, 0.3);
}
.slider.tp-bullets:before {
	content:\" \";
	position:absolute;
	width:100%;
	height:100%;
	background:transparent;
	margin-left:-10px;
    margin-top:-10px;
	box-sizing:content-box;
}
.slider .tp-bullet {
	position:absolute;
	border-radius:50%;
	cursor: pointer;
	box-sizing:content-box;
    background-color: transparent;
    width: 14px;
    height: 14px;
}
.slider .tp-bullet:before {
	position: absolute;
    display: block;
    content: \'\';
    left: 0;
    top: 0;
    bottom: 0;
    right: 0;
    border: 1px solid #333;
	border-radius: 50%;
    transform: scale(0.7);
    opacity: 0;
    transition: all 0.3s;
}
.slider .tp-bullet .tp-bullet-bg {
	width: 8px;
    height: 8px;
    margin: 3px;
	background-color: #e0e0e0;
    display: block;
	border-radius:50%;
    transition: all 0.3s;
}
.slider .tp-bullet:hover,
.slider .tp-bullet.selected {
	background-color: transparent;
}
.slider .tp-bullet:hover .tp-bullet-bg,
.slider .tp-bullet.selected .tp-bullet-bg {
	background-color: transparent;
}
.slider .tp-bullet:hover:before,
.slider .tp-bullet.selected:before {
	opacity: 1;
    transform: scale(1);
}"}
