wp_revslider_navigations.css:
{"bullets":".custom-01 .tp-bullet {
-webkit-border-radius: 50%;
-moz-border-radius: 50%;
-o-border-radius: 50%;
border-radius: 50%;
width: 14px;
height: 14px;
padding: 3px;
background-clip: content-box;
background-color: rgba(51,51,51,0.3);
-webkit-transition: all 0.3s;
-moz-transition: all 0.3s;
-o-transition: all 0.3s;
transition: all 0.3s;
}
.custom-01 .tp-bullet:before {
display: block;
position: absolute;
content: \'\';
border: 1px solid #333;
right: 0;
bottom: 0;
left: 0;
top: 0;
-webkit-transform: scale(0.6);
-moz-transform: scale(0.6);
-o-transform: scale(0.6);
transform: scale(0.6);
opacity: 0;
-webkit-border-radius: 50%;
-moz-border-radius: 50%;
-o-border-radius: 50%;
border-radius: 50%;
-webkit-transition: all 0.3s;
-moz-transition: all 0.3s;
-o-transition: all 0.3s;
transition: all 0.3s;
}
.custom-01 .tp-bullet:hover,
.custom-01 .tp-bullet.selected {
background-color: transparent;
}
.custom-01 .tp-bullet:hover:before,
.custom-01 .tp-bullet.selected:before {
-webkit-transform: scale(1);
-moz-transform: scale(1);
-o-transform: scale(1);
transform: scale(1);
opacity: 1;
}"}
