.slider-01.tparrows {
	width: 64px;
    height: 64px;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border-radius: 50%;
    background-color: transparent;
    -webkit-transition: all 0.3s;
    -moz-transition:    all 0.3s;
    -ms-transition:     all 0.3s;
    -o-transition:all 0.3s;
    transition:         all 0.3s;
}
.slider-01.tparrows:hover {
	background-color: #fff;
}
.rs-arrows-preview .slider-01.tparrows {
	top: 50%;
    -webkit-transform: (translateY(-50%));
    -moz-transform: 	 (translateY(-50%));
    -ms-transform: 	 (translateY(-50%));
    -o-transform: 	 (translateY(-50%));
    transform: (translateY(-50%));
}
.rs-arrows-preview .slider-01.tp-leftarrow {
	left: 35px;
}
.rs-arrows-preview .slider-01.tp-rightarrow {
	right: 35px;
}
.slider-01.tparrows.tp-leftarrow:before {
    content: \'\\e629\';
}
.slider-01.tparrows.tp-rightarrow:before {
    content: \'\\e628\';
}
.slider-01.tparrows:before {
    font-family: \'themify\';
    font-size: 24px;
    color: #333;
    display: block;
    line-height: 64px;
    text-align: center;
}

.slider-05.tp-bullets:before {
	content:\" \";
	position:absolute;
	width:100%;
	height:100%;
	background:transparent;
	margin-left:-10px;
    margin-top:-10px;
	box-sizing:content-box;
}
.slider-05 .tp-bullet {
	position:absolute;
	border-radius:50%;
	cursor: pointer;
	box-sizing:content-box;
    background-color: transparent;
    width: 14px;
    height: 14px;
}
.slider-05 .tp-bullet:before {
	position: absolute;
    display: block;
    content: \'\';
    left: 0;
    top: 0;
    bottom: 0;
    right: 0;
    border: 1px solid #333;
	border-radius: 50%;
    transform: scale(0.7);
    opacity: 0;
    transition: all 0.3s;
}
.slider-05 .tp-bullet .tp-bullet-bg {
	width: 8px;
    height: 8px;
    margin: 3px;
	background-color: rgba(51, 51, 51, 0.3);
    display: block;
	border-radius:50%;
    transition: all 0.3s;
}
.slider-05 .tp-bullet:hover,
.slider-05 .tp-bullet.selected {
	background-color: transparent;
}
.slider-05 .tp-bullet:hover .tp-bullet-bg,
.slider-05 .tp-bullet.selected .tp-bullet-bg {
	background-color: transparent;
}
.slider-05 .tp-bullet:hover:before,
.slider-05 .tp-bullet.selected:before {
	opacity: 1;
    transform: scale(1);
}

