.slider-04.tparrows {
	cursor:pointer;
	background-color: #fff;
	position:absolute;
	display:block;
	z-index:100;
    height: 140px;
    line-height: 140px;
    width: 140px;
    -webkit-transition: all 0.3s;
    -moz-transition: all 0.3s;
    transition: all 0.3s;
}
.slider-04.tparrows:after {
	content: \'\';
    display: block;
    position: absolute;
    top: 50%;
    width: 1px;
    height: 80px;
    transform: translateY(-50%);
    -webkit-transform: translateY(-50%);
    -moz-transform: translateY(-50%);
    background-color: #e0e0e0;
}
.slider-04.tp-leftarrow:after {
	right: 0;
}
.slider-04.tp-rightarrow:after {
	left: 0;
}
.slider-04.tparrows:hover {
	background-color: #e0e0e0;
}
.slider-04.tparrows:hover + .tparrows:after {
	display: none;
}
